#!/usr/bin/perl
#use strict;
use Infoblox;

$ENV{{'PERL_LWP_SSL_VERIFY_HOSTNAME'}} = 0;



#Info from parameters
my $hostname = $ARGV[0] . "."  . $ARGV[1];
my $network = $ARGV[2] . "/" . $ARGV[3];


my $session = Infoblox::Session->new(
        master => "$ARGV[4]",
        password => "$ARGV[6]",
        username => "$ARGV[5]",
        timeout => 180,
);

my @ipam_objects = $session->get( 'object'     => 'Infoblox::IPAM::Address',
                                  'network'  => $network,
                                  'status' => 'unused', );


if (scalar(@ipam_objects) == 0)
{{
 die("No free IP addresses for: $network");
}}

$ipaddr =  $ipam_objects[0]->ip_address;

my $host = Infoblox::DNS::Host->new(
        name => "$hostname",
        ipv4addrs => ["$ipaddr"]);

$newaddr = $host->ipv4addrs();

$session->add($host)
     or die("Add host record failed: ",
            $session->status_code() . ":" . $session->status_detail());
      print "$ipaddr";
