Script.HardcodedValue:
param(
  [String]$vCenterAddress
 ,[String]$vCenterProtocol
 ,[String]$vCenterPort
 ,[String]$vCenterUsername
 ,[String]$vCenterPassword
 ,[String]$vCenterDatacenter
)

trap { 
  "`nError Information`n=================`nCategory: {0}`nID: {2}`nType: {1}`nMessage: {3}" -f
  $_.CategoryInfo.Category, $_.Exception.GetType().FullName,
  $_.FullyQualifiedErrorID, $_.Exception.Message
  $_.ErrorDetails
  $_
  $LASTEXITCODE = 0
  $ERROR.clear()
  exit 0
}

$ErrorActionPreference = "Stop"

Add-PSSnapin VMware.VimAutomation.Core

Connect-VIServer -Server $vCenterAddress -Port $vCenterPort -Protocol $vCenterProtocol -User $vCenterUsername -Password $vCenterPassword -WarningAction SilentlyContinue | Out-Null

try {
  $ErrorActionPreference = "Stop"

  function Get-InventoryPath {
    param ($Object)
    $foldermark = 0

    $Path = $Object.Name
    do {
      if ($Object.Id -cmatch "Folder") {
        $Parent = (Get-Folder $Object).parent
      }
      else {
        $Parent = (Get-View $Object).parent
        if ( $Parent -cmatch "Folder" ) {
          $foldermark = 1
        }
        elseif ( $foldermark ) {
          # a non-folder just to the left of a folder:
          # remove the folder off the start of the path
          # this will also remove the Datacenters/
          $Path = ( $Path.split( '/', 2 ) )[-1]
          $foldermark = 0
        }
      }
      if ($Parent -ne $null) {
        if ($Parent.Id -cmatch "Folder") {
          $Path = (Get-Folder $Parent).Name + "/" + $Path
        }
        else {
          $Path = (Get-View $Parent).Name + "/" + $Path
        }
        $Object = $Parent
      }
    } while ($Parent -ne $null)

    Write-Output $Path
  }

  function Get-ParentDatacenterId {
    param ($Object)
    $Parent = (Get-View $Object).parent
    $Datacenter = $null 
    do {
      if ($Parent.Type -match "Datacenter") {
        $Datacenter = get-datacenter  -id "$Parent"
      }
      else {
        $Parent = (Get-View $Parent).parent
      }
    } while ($Datacenter -eq $null)
    Write-Output ($Datacenter | Get-View).MoRef.Value
  }

  # Make sure we have a vCenter and PowerCLI which supports SDRS
  # API > 4 and PowerCLI > 5.1
  $si = Get-View ServiceInstance
  $apiMajor = ($si.content.about.apiversion).split(".")[0]
  $cliVersion = Get-PowerCLIVersion
  $sdrsSupport = $true 

  if (($apiMajor -lt 5) -or (($cliVersion.major -lt 6) -and (($cliVersion.major -eq 5) -and ($cliVersion.minor -lt 1)))) {
    $sdrsSupport = $false #This environment does support datastore clusters
  } 

  if ($vCenterDatacenter -eq "*") {
    $datastores = Get-Datastore 
    if ($sdrsSupport) {
      $datastoreclusters = Get-DatastoreCluster -ErrorAction SilentlyContinue
    }
  }
  else {
    $datastores = Get-Datastore -Datacenter "$vCenterDatacenter"
    if ($sdrsSupport) {
      $datastoreclusters = Get-DatastoreCluster  -Location (Get-Datacenter -Name $vCenterDatacenter) -ErrorAction SilentlyContinue
    }
  } 
  Write-Output '<results>' 
  if (($datastoreclusters -ne $null)) {
    foreach($datastorecluster in $datastoreclusters) {
      if (($datastorecluster.extensiondata.childentity).count -ne 0 ) {
        $datacenterId = $(Get-ParentDatacenterId $datastorecluster)
        $vmhosts = ""
        $vmhostlist = (get-datastore -id "Datastore-$($datastorecluster.extensiondata.childentity[0].value)").ExtensionData.Host
        foreach ($value in ($datastorecluster.extensiondata.childentity)) {
          $clustereddatastores += @{($value.Type + '-' + $value.value) = $datastorecluster}
        }
        foreach ($vmhost in $vmhostList) { $vmhosts += ($vmhost.Key.Type + '-' + $vmhost.Key.Value + ',') }
        Write-Output '<row>'
#         [System.String]::Concat('    <Name>',$vCenterAddress,':',$datastorecluster.Id,'</Name>')
        [System.String]::Concat('    <MORef>',($datastorecluster | Get-View).Moref.Value,'</MORef>')
        [System.String]::Concat('    <UniqueID>',($datastorecluster | Get-View).Moref.Value,'</UniqueID>')
        [System.String]::Concat('    <DatastoreName>',$datastorecluster.Name,'</DatastoreName>')
        [System.String]::Concat('    <DatastorePath>', (Get-InventoryPath $datastorecluster),'</DatastorePath>')
        [System.String]::Concat('    <Capacity>', [Math]::Round($datastorecluster.CapacityGB,2),'</Capacity>')
        [System.String]::Concat('    <FreeSpace>', [Math]::Round($datastorecluster.FreeSpaceGB,2),'</FreeSpace>')
        [System.String]::Concat('    <FullThreshold/>')
        [System.String]::Concat('    <Hosts>',$vmhosts,'</Hosts>')
        [System.String]::Concat('    <Datacenter>', $datacenterId,'</Datacenter>')
        [System.String]::Concat('    <vCenterName>', $vCenterAddress,'</vCenterName>')
        [System.String]::Concat('    <DatastoreCluster>Yes</DatastoreCluster>')
        [System.String]::Concat('    <DatastoreClusterID>',$vCenterAddress,':',($datastorecluster | Get-View).Moref.Value,'</DatastoreClusterID>')
        Write-Output '</row>'
      }
    }
  }
  foreach($datastore in $datastores) {
    $vmhosts = ""
    $datacenterId = $(Get-ParentDatacenterId $datastore)
    foreach ($vmhost in $datastore.ExtensionData.Host) { $vmhosts += ($vmhost.Key.Type + '-' + $vmhost.Key.Value + ',') }
    $capacity = [Math]::Round( ( $datastore.CapacityMB / 1024 ), 2 )
    $freespace = [Math]::Round( ( $datastore.FreeSpaceMB / 1024 ), 2 )
    Write-Output '<row>'
#     [System.String]::Concat('    <Name>',$vCenterAddress,':',$datastore.Id,'</Name>')
    [System.String]::Concat('    <MORef>',($datastore | Get-View).Moref.Value,'</MORef>')
    [System.String]::Concat('    <UniqueID>',($datastore | Get-View).Moref.Value,'</UniqueID>')
    [System.String]::Concat('    <DatastoreName>',$datastore.Name,'</DatastoreName>')
    [System.String]::Concat('    <DatastorePath>', (Get-InventoryPath $datastore),'</DatastorePath>')
    [System.String]::Concat('    <Capacity>', $capacity,'</Capacity>')
    [System.String]::Concat('    <FreeSpace>', $freespace,'</FreeSpace>')
    [System.String]::Concat('    <FullThreshold/>')
    [System.String]::Concat('    <Hosts>',$vmhosts,'</Hosts>')
    [System.String]::Concat('    <Datacenter>', $datacenterId,'</Datacenter>')
    [System.String]::Concat('    <vCenterName>', $vCenterAddress,'</vCenterName>')
    [System.String]::Concat('    <DatastoreCluster>No</DatastoreCluster>')
    if ( ($clustereddatastores -ne $null) -and $clustereddatastores.containskey($datastore.Id)) {
      [System.String]::Concat('    <DatastoreClusterID>',$vCenterAddress,':',($clustereddatastores.Get_Item($datastore.Id)).Id,'</DatastoreClusterID>')
    }
    else {
      [System.String]::Concat('    <DatastoreClusterID></DatastoreClusterID>')
    }
    Write-Output '</row>'
  }
  Write-Output '</results>'
}
finally {
  disconnect-VIServer -Confirm:$false
}
