Script.HardcodedValue:
param(
  [String]$vCenterAddress
 ,[String]$vCenterProtocol
 ,[String]$vCenterPort
 ,[String]$vCenterUsername
 ,[String]$vCenterPassword
 ,[String]$vDatacenter
 ,[String]$vCluster
 ,[String]$Capacity
 ,[String]$VendorID
 ,[String]$DatastoreName
 ,[String]$Format
)

trap { 
  "`nError Information`n=================`nCategory: {0}`nID: {2}`nType: {1}`nMessage: {3}" -f
  $_.CategoryInfo.Category, $_.Exception.GetType().FullName,
  $_.FullyQualifiedErrorID, $_.Exception.Message
  $_.ErrorDetails
  $_
  $LASTEXITCODE = 0
  $ERROR.clear()
  exit 0
}

$ErrorActionPreference = "Stop"

Add-PSSnapin VMware.VimAutomation.Core

Connect-VIServer -Server $vCenterAddress -Port $vCenterPort -Protocol $vCenterProtocol -User $vCenterUsername -Password $vCenterPassword -WarningAction SilentlyContinue | Out-Null

try {
  $ErrorActionPreference = "Stop"


  # Make sure we have a vCenter and PowerCLI which supports SDRS
  # API > 4 and PowerCLI > 5.1
  $si = Get-View ServiceInstance
  $apiMajor = ($si.content.about.apiversion).split(".")[0]
  $cliVersion = Get-PowerCLIVersion
  $sdrsSupport = $true 

  if (($apiMajor -lt 5) -or (($cliVersion.major -lt 6) -and (($cliVersion.major -eq 5) -and ($cliVersion.minor -lt 1)))) {
    $sdrsSupport = $false #This environment does support datastore clusters
  } 
  $ClusterId = Get-Cluster -Name $vCluster -Location (Get-Datacenter -Name $vDatacenter)
  $vmhosts = Get-VMHost -Location $ClusterId | Where-Object { $_.ConnectionState -eq 'Connected' }
  $dstores = $vmhosts | Get-Datastore
  $table = New-Object system.Data.DataTable "DeviceUUIDs"
  $UUID = New-Object system.Data.DataColumn "UUID"
  $table.columns.add( $UUID )

  foreach( $dstore in $dstores ) {
    $dstorex = $dstore.ExtensionData.Info.Vmfs.Extent | foreach{$_.DiskName}
    $row = $table.NewRow()
    $row.$UUID = $dstorex
    $table.Rows.Add($row)
  }
  foreach ( $vmhost in $vmhosts ) {
    $res = get-VMHostStorage -VMHost $vmhost -RescanAllHba -RescanVmfs
    $disks = Get-ScsiLun -VMhost $vmhost -LunType disk
    foreach ( $disk in $disks ) {
      $newDisk = $table | Where-Object {$_.UUID -eq $disk.CanonicalName}
      if ($newDisk.UUID -eq $null) {
        if ( ($disk.CapacityGB -eq $Capacity) -and ($disk.CanonicalName -match $VendorID) ) {      
          $res = New-Datastore –Name $DatastoreName -VMHost $vmhost –Path $disk.CanonicalName –Vmfs -FileSystemVersion $Format 
          break
        }
      }
    }
    # we need to create the datastore on each host of the cluster, so no
    # 'break' here.
    # break     
  }
}
finally {
  disconnect-VIServer -Confirm:$false
}
