Script.HardcodedValue:
param(
  [String]$vCenterAddress
 ,[String]$vCenterProtocol
 ,[String]$vCenterPort
 ,[String]$vCenterUsername
 ,[String]$vCenterPassword
 ,[String]$Cluster
 ,[String]$NewResourcePool
 ,[String]$CpuReservationMhz
 ,[String]$CpuLimitMhz
 ,[String]$CpuSharesLevel
 ,[String]$MemReservationMB
 ,[String]$MemLimitMB
)

trap { 
#  "`nError Information`n=================`nCategory: {0}`nID: {2}`nType: {1}#`nMessage: {3}" -f
#  $_.CategoryInfo.Category, $_.Exception.GetType().FullName,
#  $_.FullyQualifiedErrorID, $_.Exception.Message
#  $_.ErrorDetails
#  $_
  $_.Exception.Message
  $LASTEXITCODE = 0
  $ERROR.clear()
  exit 9027
}

$ErrorActionPreference = "Stop"

Add-PSSnapin VMware.VimAutomation.Core

function Get-InventoryPath {
  param ($Object)
  $foldermark = 0

  $Path = $Object.Name
  do {
    if ($Object.Id -cmatch "Folder") {
      $Parent = (Get-Folder $Object).parent
    } 
    else {
      $Parent = (Get-View $Object).parent
       if ( $Parent -cmatch "Folder" ) {
         $foldermark = 1
       }
       elseif ( $foldermark ) {
         # a non-folder just to the left of a folder:
         # remove the folder off the start of the path
         # this will also remove the Datacenters/
         $Path = ( $Path.split( '/', 2 ) )[-1]
         $foldermark = 0
       }
    }
    if ($Parent -ne $null) { 
      if ($Parent.Id -cmatch "Folder") {
        $Path = (Get-Folder $Parent).Name + "/" + $Path
      } 
      else {
        $Path = (Get-View $Parent).Name + "/" + $Path
      }
      $Object = $Parent
    }
  } while ($Parent -ne $null)

 Write-Output $Path
}

#We are passed GB, but need MB. 
$ResultMemReservationMB = ([int]$MemReservationMB) * 1024

if ($MemLimitMB -match "-1")
{
$ResultMemLimitMB = ([int]$MemLimitMB)
}
else
{
$ResultMemLimitMB = ([int]$MemLimitMB) * 1024
}

Connect-VIServer -Server $vCenterAddress -Port $vCenterPort -Protocol $vCenterProtocol -User $vCenterUsername -Password $vCenterPassword -WarningAction SilentlyContinue | Out-Null
try {

  #Create the new resource pool
  $NewRP = New-ResourcePool -Location (get-cluster -id $Cluster) -Name $NewResourcePool -CpuExpandableReservation $true -CpuReservationMhz $CpuReservationMhz -CpuLimitMhz $CpuLimitMhz -CpuSharesLevel $CpuSharesLevel -MemExpandableReservation $true -MemReservationMB $ResultMemReservationMB -MemLimitMB $ResultMemLimitMB -MemSharesLevel normal

Write-Output '<results>'  
    Write-Output '<row>'
    [System.String]::Concat('    <ResourcePoolPath>', ( Get-InventoryPath $NewRP ).Replace( '/Resources/','/' ),'</ResourcePoolPath>')
    [System.String]::Concat('    <Moref>',$NewRP.ExtensionData.MoRef.Value,'</Moref>')
    Write-Output '</row>'
  Write-Output '</results>'
}

finally {
  disconnect-VIServer -Confirm:$false
}
