Script.HardcodedValue:
param( [String] $Parent, [String] $Folder)

$result= new-object PSObject

try {
  function Get-InventoryPath {
    param ($Object)
    $foldermark = 0

    $Path = $Object.Name
    do {
      if ($Object.Id -cmatch "Folder") {
        $Parent = (Get-Folder $Object).parent
      } 
      else {
        $Parent = (Get-View $Object).parent
        if ( $Parent -cmatch "Folder" ) {
          $foldermark = 1
        }
        elseif ( $foldermark ) {
          # a non-folder just to the left of a folder:
          # remove the folder off the start of the path
          # this will also remove the Datacenters/
          $Path = ( $Path.split( '/', 2 ) )[-1]
          $foldermark = 0
        }
      }
      if ($Parent -ne $null) { 
        if ($Parent.Id -cmatch "Folder") {
          $Path = (Get-Folder $Parent).Name + "/" + $Path
        } 
        else {
          $Path = (Get-View $Parent).Name + "/" + $Path
        }
        $Object = $Parent
      }
    } while ($Parent -ne $null)

    Write-Output $Path
  }

  $splitInventoryPath = $Parent.Split("/")
  $dcs = Get-Datacenter -Name $splitInventoryPath[1] -EA SilentlyContinue 
  if ($dcs.length -eq 0) { $dcs = Get-Datacenter -Name $splitInventoryPath[0] }
  if ($dcs.length -eq 1) 
     { $d = $dcs[0] }
  else {
     $dcPath= $splitInventoryPath[0..($splitInventoryPath.length-2)] -join '/'
     foreach($d in $dcs) {
       $p = Get-InventoryPath $d; 
       if ($p -eq '$dcPath') { break; }
     }
  }

  $f = Get-Folder -Name $Folder -Location $d -EA Stop
 # Get-VM -location $f | Shutdown-VMGuest -Confirm:$false -EA Stop
 # start-sleep -s 30
 Remove-Folder -Folder $f -Confirm:$false -DeletePermanently -EA Stop

}
catch {
    $err = $Parent + "/" + $Folder + " not found or other error deleting folder. " + $_.Exception.Message
    exit 1
}
