Script.HardcodedValue:
param( [String]$vCenterAddress ,[String]$vCenterProtocol ,[String]$vCenterPort ,[String]$vCenterUsername ,[String]$vCenterPassword ,[String]$ResourcePoolName ,[String]$CpuReservationMhz ,[String]$CpuLimitMhz ,[String]$MemReservationMB ,[String]$MemLimitMB )
trap { # "`nError Information`n=================`nCategory: {0}`nID: {2}`nType: {1}#`nMessage: {3}" -f # $_.CategoryInfo.Category, $_.Exception.GetType().FullName, # $_.FullyQualifiedErrorID, $_.Exception.Message # $_.ErrorDetails # $_ $_.Exception.Message $LASTEXITCODE = 0 $ERROR.clear() exit 9027 }
$ErrorActionPreference = "Stop"
Add-PSSnapin VMware.VimAutomation.Core
#We are passed GB, but need MB. $ResultMemReservationMB = ([int]$MemReservationMB) * 1024 $ResultMemLimitMB = ([int]$MemLimitMB) * 1024
Connect-VIServer -Server $vCenterAddress -Port $vCenterPort -Protocol $vCenterProtocol -User $vCenterUsername -Password $vCenterPassword -WarningAction SilentlyContinue | Out-Null try {
#Update resource pool $NewRP = Set-ResourcePool -ResourcePool $ResourcePoolName -CpuReservationMhz $CpuReservationMhz -CpuLimitMhz $CpuLimitMhz -MemReservationMB $ResultMemReservationMB -MemLimitMB $ResultMemLimitMB
}
finally { disconnect-VIServer -Confirm:$false }
