Script.HardcodedValue:
param( [String]$vCenterAddress ,[String]$vCenterProtocol ,[String]$vCenterPort ,[String]$vCenterUsername ,[String]$vCenterPassword ,[String]$ResourcePoolName ,[String]$ResourcePoolMoref )
trap { # "`nError Information`n=================`nCategory: {0}`nID: {2}`nType: {1}#`nMessage: {3}" -f # $_.CategoryInfo.Category, $_.Exception.GetType().FullName, # $_.FullyQualifiedErrorID, $_.Exception.Message # $_.ErrorDetails # $_ $_.Exception.Message $LASTEXITCODE = 0 $ERROR.clear() exit 9027 }
$ErrorActionPreference = "Stop"
Add-PSSnapin VMware.VimAutomation.Core
Connect-VIServer -Server $vCenterAddress -Port $vCenterPort -Protocol $vCenterProtocol -User $vCenterUsername -Password $vCenterPassword -WarningAction SilentlyContinue | Out-Null try { # Delete resource pool $rp = Get-ResourcePool -Id $ResourcePoolMoref Remove-ResourcePool $rp -Confirm:$false }
finally { disconnect-VIServer -Confirm:$false }
