Script.HardcodedValue:
param (
 [String] $vmmor,
 [String] $newpath, 
 [String] $remove)

$ErrorActionPreference = "Stop"

filter Get-FolderPath { 
  $_ | Get-View | % { 
     $row = "" | select Name, Path 
     $row.Name = $_.Name 
     $current = Get-View $_.Parent 
     # $path = $_.Name 
     $path = ""
     do { 
        $parent = $current
        if($parent.Name -ne "vm"){$path = $parent.Name + "/" + $path} 
        $current = Get-View $current.Parent 
     } while ($current.Parent -ne $null) 
     $row.Path = $path
     $row
   } 
} 

$vm=get-vm -ID $vmmor

$oldfolder=get-folder -ID $vm.FolderId

$names=$newpath.split('/')
$folders = get-folder -Name $names[$names.length-1]
if (@($folders).count -gt 0) {
  ForEach ($f in $folders) { 
    $folderPath = ($f | Get-Folderpath).Path+$names[$names.length-1]
    If ($folderPath -eq $newPath) { 
       $newfolderid = $f.id
       break
    }
  }
} 


$newfolder=get-folder -ID $newfolderid

move-vm $vm -Destination $newfolder -ErrorAction SilentlyContinue

if (($oldfolder|get-vm).count -eq 0 -and $remove) {
 remove-folder $oldfolder -confirm:$false -ErrorAction SilentlyContinue
}
